//window.GSPasswordVaultAlert = "";
//window.hibpPasswordPage = "CREATEACCOUNT"; //values > CREATEACCOUNT | PASSWORDRESET | CHANGEPASSWORD

$(document).ready(function(){
	//jwatts 3/27/2017
	//The functions below:
	// - add some event handlers for the password fields
	// - check the password against the www.HaveIBeenPwned.com API
	// - show the system option message if the password is weak
	//A couple of global variables (lines 1 and 2 above) have been added to the
	// page and are used by the script below. 
	//This script is used on 3 pages, indicated by the values above.
	
	var msgtxt = (window && window.GSPasswordVaultAlert) ? window.GSPasswordVaultAlert : "Check password strength";
		
	//Only runs if the page name was set and the "haveIBeenPwned" method is available.
	if (window && window.hibpPasswordPage && window.haveIBeenPwned) {
		if (window.hibpPasswordPage === "CREATEACCOUNT") handleCREATEACCOUNT();
		else if (window.hibpPasswordPage === "PASSWORDRESET") handlePASSWORDRESET();
		else if (window.hibpPasswordPage === "CHANGEPASSWORD") handleCHANGEPASSWORD();
	}
	
	function handleCREATEACCOUNT() {
		var pwdFld = $('input[id$="_cst_new_password"]');
		var pwdParent = pwdFld.closest("div.ContentContainer");
		
		//add the message span
		var outerDiv = $("<div />").appendTo(pwdParent)
			.css("height", "50px");
		var msgDiv = $("<div />").appendTo(outerDiv)
			.css({ 
				"font-size": "12px",
				"display": "none",
				"height": "50px", 
				"padding": "8px",
				"color": "#A94442", 
				"background-color": "#F2DEDE",
				"border-color": "#EBCCD1", 
				"border": "1px solid transparent", 
				"border-radius": "4px"
			});
		pwdParent.css("margin-bottom", "15px");
		
		//common handler
		handlePwned(pwdFld, msgDiv);
	}
		
	function handleCHANGEPASSWORD() {
		var pwdFld = $('input#cst_new_password');
		var pwdParent = pwdFld.closest("div#DesignedDiv");
		
		//add the message div
		var outerMsgDiv = $("<div />").appendTo(pwdParent)
			.css("padding-top", "110px");
		var msgDiv = $("<div />").appendTo(outerMsgDiv)
			.css({ 
				"font-size": "12px",
				"display": "none",
				"height": "40px", 
				"padding": "8px",
				"color": "#A94442", 
				"background-color": "#F2DEDE",
				"border-color": "#EBCCD1", 
				"border": "1px solid transparent", 
				"border-radius": "4px"
			});
		pwdParent.css("height", "170px");
		
		//common handler
		handlePwned(pwdFld, msgDiv);
	}
	
	function handlePASSWORDRESET() {
		//same as CHANGEPASSWORD
		handleCHANGEPASSWORD();
	}
	
	function handlePwned(pwdFld, msgElement) {
		var lastPwd = null;
		
		pwdFld.blur(function() {
			lastPwd = pwdFld.val();			
			if (lastPwd !== "") {
				//cue the magic!
				haveIBeenPwned(lastPwd, pwnedResponse);
			}
		});
		pwdFld.on("keypress keyup click focus", function(e) {
			if (lastPwd != pwdFld.val()) {
				msgElement.text("").hide();
			}
		});
		
		function pwnedResponse(isPwned, matchCount, responseText) {
			if (isPwned) 
				msgElement.html(msgtxt).show();
		}
	}
	
});



